import axios from"axios";import{tracesUrl,articlesUrl,chatroomsUrl,visionUrl,visionaryUrl,supportUrl}from"../api";export const loadTraces=()=>async a=>{const t=await axios.get(tracesUrl()),r=await axios.get(articlesUrl()),i=await axios.get(chatroomsUrl()),o=await axios.get(visionUrl()),s=await axios.get(visionaryUrl()),l=await axios.get(supportUrl());a({type:"FETCH_TRACES",payload:{trace:t.data.data,articles:r.data.data,chatrooms:i.data.data,vision:o.data.data,visionary:s.data.data,support:l.data.data}})};